import { slide as BurgerMenu } from 'react-burger-menu';
import { Link } from 'react-router-dom';

class App extends Component {
    componentDidMount() {
        const bmMenuWrap = document.getElementById('bm-menu-wrap');
        if (bmMenuWrap) {
            bmMenuWrap.style.backgroundColor = 'red'; // Change la couleur de fond de bm-menu-wrap en rouge
        }
    }

    render() {
        return (
            <div className="nav">
                <BurgerMenu right>
                    <Link to="/" className="menu-item">Accueil</Link>
                    <Link to="/about" className="menu-item">A propos</Link>
                    <Link to="/contact" className="menu-item">Contact</Link>
                </BurgerMenu>
            </div>
        );
    }
};
