import { slide as BurgerMenu } from 'react-burger-menu';
import { useRef } from 'react';
import React, { Component } from 'react';

class App extends Component {
    componentDidMount() {
        const bmMenuWrap = document.getElementById('bm-menu-wrap');
        if (bmMenuWrap) {
            bmMenuWrap.style.backgroundColor = 'red'; // Change la couleur de fond de bm-menu-wrap en rouge
        }
    }

    render() {
        const refprojet_container = useRef(null);

        const handleClick = () => {
            refprojet_container.current?.scrollIntoView({ behavior: 'smooth' });
        };

        const refservice_Service_Container = useRef(null);

        const handleClickrefservice_Container = () => {
            refservice_Service_Container.current?.scrollIntoView({ behavior: 'smooth' });
        };

        const refservice_Contact_Container = useRef(null);

        const handleClickrefContact_Container = () => {
            refservice_Contact_Container.current?.scrollIntoView({ behavior: 'smooth' });
        };
        return (
            <div className="nav">
                <BurgerMenu right>
                    <button onClick={handleClick} className="menu-item">Accueil</button>
                    <button to="/about" className="menu-item">Project</button>
                    <button to="/contact" className="menu-item">Services</button>
                    <button to="/contact" className="menu-item">Contact</button>
                    <button to="/contact" className="menu-item">Github</button>
                </BurgerMenu>
            </div>
        );
    }
};

export default App
